import avatarka from '../image/down2.jpg';
import rerenderInfo from '../render';

let state = {
  myPage: {
    person: [
      { name: 'Sasha', id: '1' },
      { name: 'Tyom', id: '2' },
      { name: 'Viktoria', id: '3' },
      { name: 'Ann', id: '4' },
      { name: 'Ani', id: '5' },
    ],
    messages: [{ msg: 'Hi' }, { msg: 'How is your komasutra?' }, { msg: 'Yo' }],
  },
  messagePage: {
    mymsg: [
      { img: avatarka, ike: 'like', msg: 'barior axperner' },
      { img: avatarka, ike: 'like', msg: 'inchka chka' },
    ],
  },
  friendsPage: {
    friend: [{ title: 'FRIENDS' }],
  },
};

export let add = (text) => {
  let addPost = {
    msg: text,
  };
  state.myPage.messages.push(addPost);
};

export default state;
