import React from 'react';
import './App.css';

export default class App extends React.Component {
  state = {
    add: 'add',
    addText: <i class='far fa-arrow-alt-circle-left'></i>,
    delText: <i class='far fa-arrow-alt-circle-right'></i>,
    addClass: false,
    delClass: false,
    pageTitle: 'privet',
  };

  changeInput = (event) => {
    this.setState({
      pageTitle: event.target.value,
    });
  };

  listDone = (e) => {
    e.stopPropagation();
    this.setState(({ addClass }) => {
      return {
        addClass: !addClass,
      };
    });
  };
  render() {
    const newClass = 'item';

    const { add, addText, delText, delClass, addClass } = this.state;

    return (
      <div className='App'>
        <div className='btn'>
          <input
            type='text'
            className='styles'
            onChange={this.changeInput}
          ></input>
          <button className='addStyle'>{add}</button>
        </div>
        <div className='list'>
          <div
            className={`item ${this.state.addClass ? 'itemAdd' : ''} ${
              this.state.delClass ? 'itemAdd2' : ''
            } `}
          >
            <p>{this.state.pageTitle}</p>
            <p>
              <span className='span1' onClick={this.listDone}>
                {addText}
              </span>
              <span className='span2'>{delText}</span>
            </p>
          </div>
        </div>
      </div>
    );
  }
}
