import React from 'react';
import './App.css';

export default class Header extends React.Component {
  state = {
    addHaburger: <i class='fas fa-bars'></i>,
    addClass: false,
    close: <i class='fas fa-times'></i>,
  };

  addMenu = () => {
    this.setState(({ addClass }) => {
      return {
        addClass: !addClass,
      };
    });
  };

  render() {
    const { addHaburger, addClass } = this.state;
    return (
      <div className='Header'>
        <div className='hed'>
          <span className='hed2' onClick={this.addMenu}>
            {addHaburger}
          </span>
        </div>
        <div
          className={`fon ${addClass ? 'addFon' : ''}`}
          onClick={this.addMenu}
        ></div>
        <div className={`item ${addClass ? 'itemAdd' : ''}`}>
          <p>Home</p>
          <p>About</p>
          <p>Contact</p>
          <span className='spanClose' onClick={this.addMenu}>
            <i class='fas fa-times'></i>
          </span>
        </div>
      </div>
    );
  }
}
