import { ar, er } from '../../../redux/dr.js';
import Dialog from '../Dialog';
import { connect } from 'react-redux';

let mapStateToProps = (state) => {
  return {
    person: state.myPage.person,
    messages: state.myPage.messages,
    newmsg: state.myPage.newmsg,
  };
};

let mapDispatchToPro = (dispatch) => {
  return {
    AddF: () => {
      dispatch(ar());
    },
    onchmsgF: (texts) => {
      let ac = er(texts);
      dispatch(ac);
    },
  };
};

const DialogF = connect(mapStateToProps, mapDispatchToPro)(Dialog);

export default DialogF;
