import React from "react";
import n from "./New.module.css";
import * as axios from "axios";
import userPhoto from "../../image/down2.jpg";

class Users extends React.Component {
  componentDidMount() {
    axios
      .get("https://social-network.samuraijs.com/api/1.0/users")
      .then((response) => {
        this.props.setusers(response.data.items);
      });
  }

  render() {
    let tiv = Math.ceil(this.props.mklient / this.props.mpage);
    let usert = [];

    for (let i = 1; i <= tiv; i++) {
      usert.push(i);
    }

    return (
      <div className={n.item}>
        {usert.map((p) => {
          return <span className={this.props.mpageq === p && n.ba}>{p}</span>;
        })}
        {this.props.users.map((b) => {
          return (
            <div key={b.id}>
              <img src={b.photos.small != null ? b.photos.small : userPhoto} />
              <p>{b.name}</p>
              <p>{b.status}</p>
              <p>{"b.location.city"}</p>
              <p>{"b.location.country"}</p>
              {b.followed ? (
                <button onClick={() => this.props.unfolow(b.id)}>
                  unfolow
                </button>
              ) : (
                <button onClick={() => this.props.folow(b.id)}>follow</button>
              )}
            </div>
          );
        })}
      </div>
    );
  }
}

export default Users;
