import avatarka from '../image/down2.jpg';
import MR from './mr';
import DDR from './dr';

let store = {
  _rrerenderInfo() {},

  _state: {
    myPage: {
      person: [
        { name: 'Sasha', id: '1' },
        { name: 'Tyom', id: '2' },
        { name: 'Viktoria', id: '3' },
        { name: 'Ann', id: '4' },
        { name: 'Ani', id: '5' },
      ],
      messages: [
        { msg: 'Hi' },
        { msg: 'How is your komasutra?' },
        { msg: 'Yo' },
      ],
      newmsg: 'Hi',
    },
    messagePage: {
      mymsg: [
        { img: avatarka, ike: 'like', msg: 'barior axperner', id: 1 },
        { img: avatarka, ike: 'like', msg: 'inchka chka', id: 2 },
      ],
      adText: 'yo yo',
    },
    friendsPage: {
      friend: [{ title: 'FRIENDS' }],
    },
  },

  getStat() {
    return this._state;
  },
  subscribe(observer) {
    this._rrerenderInfo = observer;
  },

  dispatch(action) {
    this._state.myPage = MR(this._state.myPage, action);
    this._state.messagePage = DDR(this._state.messagePage, action);

    this._rrerenderInfo(this._state);
  },
};

export default store;
