import avatarka from '../image/down2.jpg';

const HR = 'ADD-POST';
const HRR = 'UPDATE-NEW-POST-TEXT';

const MR = (state, action) => {
  switch (action.type) {
    case HR:
      let addPost = {
        msg: state.messagePage.adText,
        img: avatarka,
        ike: 'like',
      };
      state.mymsg.push(addPost);
      state.adText = '';
      return state;
    case HRR:
      this._state.messagePage.adText = action.newText;
      this._rrerenderInfo(this._state);
  }
};

export const arajin = () => ({
  type: 'ADD-POST',
});

export const erkrord = (text) => ({
  type: 'UPDATE-NEW-POST-TEXT',
  newText: text,
});

export default MR;
