import React from 'react';
import './App.css';

export default class App extends React.Component {
  state = {
    person: 'click me',
  };

  handleClick = () => {
    alert('inchka chka');
  };

  render() {
    const styles = {
      border: '3px solid black',
      width: '100px',
      height: '50px',
      cursor: 'pointer',
      background: 'black',
      color: 'white',
      fontSize: '20px',
    };
    return (
      <div className='App'>
        <button style={styles} onClick={this.handleClick}>
          {this.state.person}
        </button>
        <input type='text'></input>
      </div>
    );
  }
}
