import React from "react";
import * as axios from "axios";
import Header from "./Header";
import mapStateToProps from "react-redux/lib/connect/mapStateToProps";
import { connect } from "react-redux";
import { ath, pr } from "../../redux/authPage";

class HeaderC extends React.Component {
  componentDidMount() {
    this.props.isFetchings(true);
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${this.props.mpageq}&count=${this.props.mklient}`
      )
      .then((response) => {
        this.props.isFetchings(false);
        this.props.setusers(response.data.items);
        this.props.setTotalUserCount(response.data.totalCount);
      });
  }

  render() {
    return <Header {...this.props} />;
  }
}

const mapStateToPropss = (state) => ({
  isAuth: state.auth.isAuth,
  login: state.auth.login,
});

export default connect(mapStateToPropss, { ath })(HeaderC);
