import React from 'react';

export default class List extends React.Component {
  state = {
    add: false,
  };

  addClass = () =>
    this.setState(({ add }) => {
      return {
        add: !add,
      };
    });

  render() {
    return (
      <div className={`h6 ${this.state.add ? 'add2' : ''}`}>
        <h6 className={`${this.state.add ? 'add2' : ''}`}>
          <b>{this.props.name}</b>
        </h6>
        <div className='btn'>
          <span className='btnspan'>
            <i class='far fa-trash-alt'></i>
          </span>
          <span onClick={this.addClass}>
            <i class='far fa-check-square'></i>
          </span>
        </div>
      </div>
    );
  }
}
