import { UsersApi } from "../API/api";

const A_TH = "A_TH";

let initialState = {
  userId: null,
  email: null,
  login: null,
  isAuth: false,
};

const authPage = (state = initialState, action) => {
  switch (action.type) {
    case A_TH:
      return { ...state, ...action.data, isAuth: true };
    default:
      return state;
  }
};

export const ath = (id, email, login) => ({
  type: A_TH,
  data: { id, email, login },
});

export default authPage;
