import React from 'react';
import './App.css';

export default class App extends React.Component {
  state = {
    add: 'add',
    addText: <i class='far fa-arrow-alt-circle-left'></i>,
    delText: <i class='far fa-arrow-alt-circle-right'></i>,
    addClass: false,
    pageTitle: 'privet',
  };

  changeInput = (event) => {
    this.setState({
      pageTitle: event.target.value,
    });
  };

  changeList = () => {
    this.setState({
      addClass: true,
    });
  };

  render() {
    const newClass = 'item';

    const { addClass } = this.state;

    if (addClass) {
      newClass += 'item2';
    }

    const { add, addText, delText } = this.state;

    return (
      <div className='App'>
        <div className='btn'>
          <input
            type='text'
            className='styles'
            onChange={this.changeInput}
          ></input>
          <button className='addStyle'>{add}</button>
        </div>
        <div className='list'>
          <div className={newClass}>
            <p>{this.state.pageTitle}</p>
            <p>
              <span className='span1'>{addText}</span>
              <span className='span2' onClick={this.changeList}>
                {delText}
              </span>
            </p>
          </div>
        </div>
      </div>
    );
  }
}
