import React from 'react';

export default class ToDo extends React.Component {
  state = {
    name: '',
  };

  newToDo = () => {
    this.setState((ev) => {
      return {
        name: ev.toggle.value,
      };
    });
  };

  render() {
    return (
      <div className='list'>
        <div className='submit'>
          <input type='text' onChange={this.state.newToDo} />
          <button>
            <b> Add</b>
          </button>
        </div>
        <div className=''>
          <h6>{this.state.name}</h6>
        </div>
      </div>
    );
  }
}
