import React from "react";
import d from "./Dialog.module.css";
import Dialogitem from "./Dialogitem/Dialogitem";
import Message from "./Message/Message.jsx";
import { Field, reduxForm } from "redux-form";
import { maxLengthCreatre, recuired } from "../common/validators/validators";

let Dialog = (props) => {
  let addNewText = (values) => {
    return props.AddF(values.textarea);
  };
  console.log(addNewText);
  return (
    <div className={d.item}>
      <div className={d.dialogs}>
        {props.person.map((item) => {
          return <Dialogitem name={item.name} id={item.id} />;
        })}
      </div>
      <div className={d.message}>
        {props.messages.map((ms) => {
          return <Message name={ms.msg} />;
        })}
      </div>
      <TextareaForm onSubmit={addNewText} />
    </div>
  );
};

let MXlght = { maxLengthCreatre };

const FormTeaxtarea = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
      <Field
        name={"textarea"}
        component={"textarea"}
        validate={[recuired, MXlght]}
        placeholder={"Massage"}
      />
      <button>add</button>
    </form>
  );
};
const TextareaForm = reduxForm({
  form: "textarea",
})(FormTeaxtarea);

export default Dialog;
