import React from "react";
import s from "./formscontrols.module.css";

export const Textarea = (input, meta, ...props) => {
  return (
    <div className={s.FormC + " " + s.error}>
      <textarea {...input} {...props} />
      {meta.touched && meta.error && <span>{props.validate}ba</span>}
    </div>
  );
};
