import {useState} from "react";

const D_R = 'BRAT_JAN';
const D_R_R = 'AB_JAN';

let initialState = {
  person: [
    { name: 'Sasha', id: '1' },
    { name: 'Tyom', id: '2' },
    { name: 'Viktoria', id: '3' },
    { name: 'Ann', id: '4' },
    { name: 'Ani', id: '5' },
  ],
  messages: [{ msg: 'Hi' }, { msg: 'How is your komasutra?' }, { msg: 'Yo' }],
  newmsg: 'Hi',
};

const DDRS = (state = initialState, action) => {
  switch (action.type) {
    case D_R: {
      let addMsg = {
        msg: state.newmsg,
      };
    
      return { ...state, messages: [...state.messages], addMsg, newmsg: '' };
    }
    case D_R_R:
      return { ...state, newmsg: action.newText };

    default:
      return state;
  }
};

export const ar = () => ({
  type: D_R,
});

export const er = (text) => ({
  type: D_R_R,
  newMsg: text,
});

export default DDRS;
