const D_R = "BRAT_JAN";

let initialState = {
  userId: null,
  email: null,
  login: null,
};

const DDRS = (state = initialState, action) => {
  switch (action.type) {
    case D_R_R:
      return { ...state, newmsg: action.newText };

    default:
      return state;
  }
};

export const ar = () => ({
  type: D_R,
});

export const er = (text) => ({
  type: D_R_R,
  newMsg: text,
});
export const pr = (profile) => ({
  type: P_R_C,
  profile,
});

export default DDRS;
