import React from 'react';
import icon1 from './Images/icon-1.png';
import icon2 from './Images/icon-2.png';
import icon3 from './Images/icon-3.png';
import icon4 from './Images/icon-4.png';
import icon5 from './Images/icon-5.png';
import icon6 from './Images/icon-6.png';

export default class Menu4 extends React.Component {
  state = {
    cont: [
      {
        img: <img src={icon1} />,
        h4: 'Shared Hosting',
        p:
          'Donec nec justo eget felis facilisis fermentum. Aliquam porttitor mauris sit amet orci.',
      },
      {
        img: <img src={icon2} />,
        h4: 'Dedicated Hosting',
        p:
          'Donec nec justo eget felis facilisis fermentum. Aliquam porttitor mauris sit amet orci.',
      },
      {
        img: <img src={icon3} />,
        h4: 'Domain Registration',
        p:
          'Donec nec justo eget felis facilisis fermentum. Aliquam porttitor mauris sit amet orci.',
      },
    ],
    cont2: [
      {
        img: <img src={icon4} />,
        h4: 'Shared Hosting',
        p:
          'Donec nec justo eget felis facilisis fermentum. Aliquam porttitor mauris sit amet orci.',
      },
      {
        img: <img src={icon5} />,
        h4: 'Dedicated Hosting',
        p:
          'Donec nec justo eget felis facilisis fermentum. Aliquam porttitor mauris sit amet orci.',
      },
      {
        img: <img src={icon6} />,
        h4: 'Domain Registration',
        p:
          'Donec nec justo eget felis facilisis fermentum. Aliquam porttitor mauris sit amet orci.',
      },
    ],
  };

  onDelete = (index) => {
    const newTodo = [...this.state.cont];
    newTodo.splice(index, 1);

    this.setState(({ todoList }) => {
      return {
        cont: newTodo,
      };
    });
    // this.setState({
    //   todoList: newTodo
    // })
  };
  render() {
    return (
      <section className='menu4'>
        <h3>
          <span>Our</span> Service
        </h3>
        <p>
          Lorem ipsum dolor sittem ametamngcing elit, per sed do eiusmoad
          teimpor sittem elit inuning ut sed.
        </p>
        <div className='menu44 menu444'>
          {this.state.cont.map((item) => {
            return (
              <div className='menu4cont'>
                <p>{item.img}</p>
                <h3>{item.h4}</h3>
                <p>
                  <b> {item.p}</b>
                </p>
              </div>
            );
          })}
        </div>
        <div className='menu44'>
          {this.state.cont2.map((item2) => {
            return (
              <div className='menu4cont'>
                <p>{item2.img}</p>
                <h3>{item2.h4}</h3>
                <p>
                  <b> {item2.p}</b>
                </p>
              </div>
            );
          })}
        </div>
        <div className='btn'>Delete</div>
      </section>
    );
  }
}
