const FOLOW = "FOLOW";
const UNFOLOW = "UNFOLOW";
const SET_USERS = "SET_USERS";
const SET_USERS_CH = "SET_USERS_CH";
const SET_USERS_MK = "SET_USERS_MK";

let initialState = {
  users: [],
  mpage: 5,
  mklient: 0,
  mpageq: 1,
};

const usersReducer = (state = initialState, action) => {
  switch (action.type) {
    case FOLOW:
      return {
        ...state,
        users: state.users.map((dd) => {
          if (dd.id === action.usersID) {
            return { ...dd, followed: true };
          }
          return dd;
        }),
      };
    case UNFOLOW:
      return {
        ...state,
        users: state.users.map((ddd) => {
          if (ddd.id === action.usersID) {
            return { ...ddd, followed: false };
          }
          return ddd;
        }),
      };
    case SET_USERS:
      return { ...state, users: action.users };
    case SET_USERS_CH:
      return { ...state, mpageq: action.mpageq };
    case SET_USERS_MK:
      return { ...state, mklient: action.mklient };
    default:
      return state;
  }
};

export const fl = (usersID) => ({ type: FOLOW, usersID });
export const uw = (usersID) => ({ type: UNFOLOW, usersID });
export const su = (users) => ({ type: SET_USERS, users });
export const ch = (mpageq) => ({ type: SET_USERS_CH, mpageq });
export const mk = (mklient) => ({ type: SET_USERS_MK, mklient: mklient });

export default usersReducer;
