import avatarka from '../image/down2.jpg';

let store = {
  _rrerenderInfo() {},

  _state: {
    myPage: {
      person: [
        { name: 'Sasha', id: '1' },
        { name: 'Tyom', id: '2' },
        { name: 'Viktoria', id: '3' },
        { name: 'Ann', id: '4' },
        { name: 'Ani', id: '5' },
      ],
      messages: [
        { msg: 'Hi' },
        { msg: 'How is your komasutra?' },
        { msg: 'Yo' },
      ],
    },
    messagePage: {
      mymsg: [
        { img: avatarka, ike: 'like', msg: 'barior axperner', id: 1 },
        { img: avatarka, ike: 'like', msg: 'inchka chka', id: 2 },
      ],
      adText: 'yo yo',
    },
    friendsPage: {
      friend: [{ title: 'FRIENDS' }],
    },
  },

  getStat() {
    return this._state;
  },
  subscribe(observer) {
    this._rrerenderInfo = observer;
  },

  addPost(text) {
    this._state.messagePage.adText = text;
    this._rrerenderInfo(this._state);
  },

  dispatch(action) {
    if (action.type === 'ba') {
      let addPost = {
        msg: this._state.messagePage.adText,
        img: avatarka,
        ike: 'like',
      };
      this._state.messagePage.mymsg.push(addPost);
      this._state.messagePage.adText = '';
      this._rrerenderInfo(this._state);
    }
  },
};

export default store;
