import { authApi } from "../API/api";

const A_TH = "A_TH";

let initialState = {
  userId: null,
  email: null,
  login: null,
  isAuth: false,
};

const authPage = (state = initialState, action) => {
  switch (action.type) {
    case A_TH:
      return { ...state, ...action.data, isAuth: true };
    default:
      return state;
  }
};

export const ath = (id, email, login) => ({
  type: A_TH,
  data: { id, email, login },
});

export const VUheader = () => (dispatch) => {
  authApi.getUsers4().then((data) => {
    if (data.resultCode === 0) {
      let { id, email, login } = data.data;
      dispatch(ath(id, email, login));
    }
  });
};
export const authLoginPAge = (email, password, rememberMe) => (dispatch) => {
  authApi.authLogin(email, password, rememberMe).then((data) => {
    if (data.resultCode === 0) {
      dispatch(VUheader());
    }
  });
};

export const Loginauth = (id, email, login) => (dispatch) => {
  authApi.authLogin().then((data) => {
    if (data.resultCode === 0) {
      dispatch(ath(id, email, login));
    }
  });
};

export default authPage;
