import avatarka from '../image/down2.jpg';

const H_R = 'ADD-POST';
const H_R_R = 'UPDATE-NEW-POST-TEXT';

const MR = (state, action) => {
  switch (action.type) {
    case H_R:
      let addPost = {
        msg: state.adText,
        img: avatarka,
        ike: 'like',
      };
      state.mymsg.push(addPost);
      state.adText = '';
      return state;
    case H_R_R:
      state.adText = action.newText;
  }
};

export const arajin = () => ({
  type: H_R,
});

export const erkrord = (text) => ({
  type: H_R_R,
  newText: text,
});

export default MR;
