import avatarka from "../image/down2.jpg";

let initialState = {
  mymsg: [
    { img: avatarka, ike: "like", msg: "barior axperner", id: 1 },
    { img: avatarka, ike: "like", msg: "inchka chka", id: 2 },
  ],
};

const ADD_POST = "ADD_POST";
const H_R_R = "UPDATE-NEW-POST-TEXT";

const messageReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD - POST: {
      let body = action.text;
      return {
        ...state,
        mymsg: [
          ...state.mymsg,
          { id: 2, msg: body, img: avatarka, ike: "like" },
        ],
      };
    }
    case H_R_R: {
      return { ...state, adText: action.newText };
    }
    default:
      return state;
  }
};

export const arajin = (text) => ({
  type: ADD - POST,
  text,
});

export default messageReducer;
