// import People from './People';


export default class Person extends React.Component{

state = {
    style: {
        backgroundColor: "red"
      }
}
    

    enterPerson = () => {
        this.setState({
          style: {
            backgroundColor: "blue"
          }
        })
      }
    render() {

       const style = {
           border: "2px solid",
           width: "250px",
           margin: "0 auto",
        
       } 

    return (
        <div style={style}  >
            <h2>{this.props.name}</h2>
           <p>
      {this.props.proff}
        </p>
        <strong>{this.props.adress}</strong>
        <p>{this.props.description}</p>
        {/* <People /> */}
        <button onClick={this.eterPerson}>click</button>
        </div>
    )
    }
}
