import { connect } from "react-redux";
import { ch, fch, fl, setTotalUserCountAC, su, uw } from "../../redux/users";
import React from "react";
import * as axios from "axios";
import Users from "./New";
import loader from "../../image/loader.png";

class UsersCn extends React.Component {
  componentDidMount() {
    this.props.isFetchings(true);
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${this.props.mpageq}&count=${this.props.mklient}`
      )
      .then((response) => {
        this.props.isFetchings(false);
        this.props.setusers(response.data.items);
        this.props.setTotalUserCount(response.data.totalCount);
      });
  }
  onPageChanged = (usermk) => {
    this.props.isFetchings(true);
    this.props.setch(usermk);
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${usermk}&count=${this.props.mklient}`
      )
      .then((response) => {
        this.props.isFetchings(false);
        this.props.setusers(response.data.items);
      });
  };

  render() {
    return (
      <>
        {this.props.isFetching ? (
          <img
            src={loader}
            style={{ position: "absolute", left: "500px", top: "300px" }}
          />
        ) : null}
        <Users
          totalUsersCount={this.props.totalUsersCount}
          mpage={this.props.mpage}
          onPageChanged={this.onPageChanged}
          users={this.props.users}
          unfolow={this.props.unfolow}
          folow={this.props.folow}
          mpageq={this.props.mpageq}
        />
      </>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersReducer.users,
    mpage: state.usersReducer.mpage,
    totalUsersCount: state.usersReducer.totalUsersCount,
    mpageq: state.usersReducer.mpageq,
    isFetching: state.usersReducer.isFetching,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    folow: (userID) => {
      dispatch(fl(userID));
    },
    unfolow: (userID) => {
      dispatch(uw(userID));
    },
    setusers: (user) => {
      dispatch(su(user));
    },
    setch: (userch) => {
      dispatch(ch(userch));
    },
    setTotalUserCount: (totalCount) => {
      dispatch(setTotalUserCountAC(totalCount));
    },
    isFetchings: (isfch) => {
      dispatch(fch(isfch));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(UsersCn);
