import { ar, er } from "../../../redux/dr.js";
import Dialog from "../Dialog";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { WithAuthRedirect } from "../../hoc/AuthRedirect";

let mapStateToProps = (state) => {
  return {
    person: state.myPage.person,
    messages: state.myPage.messages,
    newmsg: state.myPage.newmsg,
    isAuth: state.auth.isAuth,
  };
};

let mapDispatchToProps = (dispatch) => {
  return {
    AddF: () => {
      dispatch(ar());
    },
    onchmsgF: (texts) => {
      let ac = er(texts);
      dispatch(ac);
    },
  };
};

let yhyn = WithAuthRedirect(Dialog);

const DialogF = connect(mapStateToProps, mapDispatchToProps)(yhyn);

export default DialogF;
