import { profileApi } from "../API/api";

const D_R = "D_R";
const USER_PROFILE_PAGE = "USER_PROFILE_PAGE";
const CHANGE_STATUS = "CHANGE_STATUS";

let initialState = {
  person: [
    { name: "Sasha", id: "1" },
    { name: "Artyom", id: "2" },
    { name: "Viktoria", id: "3" },
    { name: "Ann", id: "4" },
    { name: "Maria", id: "5" },
  ],
  messages: [{ msg: "Hi" }, { msg: "How old are you?" }, { msg: "Yo" }],
  profile: null,
  status: "",
};

const dialogReducer = (state = initialState, action) => {
  switch (action.type) {
    case DIALOG_MESSAGES: {
      let body = action.text;
      return { ...state, messages: [...state.messages, { msg: body }] };
    }
    case USER_PROFILE_PAGE:
      return { ...state, profile: action.profile };
    case CHANGE_STATUS:
      return { ...state, status: action.status };
    default:
      return state;
  }
};

export const ar = (text) => ({
  type: D_R,
  text,
});

export const userProfilePage = (profile) => ({
  type: USER_PROFILE_PAGE,
  profile,
});

export const changeStatus = (status) => ({
  type: CHANGE_STATUS,
  status,
});

export const profilePage = (userid) => {
  return (dispatch) => {
    profileApi.getUsers5(userid).then((data) => {
      dispatch(userProfilePage(data));
    });
  };
};

export const getStatus = (userid) => (dispatch) => {
  profileApi.getStatus(userid).then((response) => {
    dispatch(changeStatus(response.data));
  });
};

export const updateStatus = (status) => (dispatch) => {
  profileApi.statusUpdate(status).then((response) => {
    if (response.data.resultCode === 0) {
      dispatch(changeStatus(status));
    }
  });
};

export default dialogReducer;
