import React from 'react';
import s from '../ProfileInfo.module.css';
import m from '../ProfileInfo.module.css';

export const Status = (props) => {
  // state = {
  //   editMode: false,
  //   status: this.props.status,
  // };

  let [editMode, setEditMode] = useState(false);
  let [status, setStatus] = useState(this.props.status);

  const activateEditMode = () => {
    setEditMode(true);
  };

  const deactivateEditMode = () => {
    setEditMode(false);
    props.updateStatus(state.status);
  };

  const statusOnchange = (e) => {
    setStatus(e.currentTarget.value);
  };

  return (
    <>
      <h2 className={s.itemP}>
        <b>STATUS</b>
      </h2>
      {!this.state.editMode && (
        <div onDoubleClick={this.activateEditMode}>
          <p>
            <b>{this.props.status || '----'}</b>
          </p>
          <button className={m.button}>
            <b>change status</b>
          </button>
        </div>
      )}
      {this.state.editMode && (
        <div>
          <input
            autoFocus={true}
            onBlur={this.deactivateEditMode}
            value={this.state.status}
            onChange={this.statusOnchange}
          />
          <span className={s.statusSpan}>
            <i className='fas fa-check'></i>
          </span>
        </div>
      )}
    </>
  );
};
