import React from 'react';
// import People from './People';

export default class Person extends React.Component {
  state = {
    addClass: false,
  };
  changePerson = () => {
    this.setState({
      addClass: true,
    });
  };

  render() {
    const { addClass } = this.state;
    const newClass = 'elem';
    if (addClass) {
      newClass += 'jhbjhbhyuj';
    }
    const style = {
      border: '2px solid',
      width: '250px',
      margin: '0 auto',
    };

    const imgWidth = {
      width: '100%',
    };
    return (
      <div style={style} className={newClass}>
        <h2>{this.props.name}</h2>
        <p>
          <img src={this.props.img} style={imgWidth} />
          {this.props.proff}
        </p>
        <strong>{this.props.adress}</strong>
        <p>{this.props.description}</p>
        <button onClick={this.props.enterPerson}>Enter person</button>
        <button onClick={this.changePerson}>change person</button>
      </div>
    );
  }
}
