import React from 'react';
import './App.css';

export default class App extends React.Component {
  state = {
    addHaburger: <i class='fas fa-bars'></i>,
    addClass: false,
    close: <i class='fas fa-times'></i>,
  };

  addMenu = () => {
    this.setState(({ addClass }) => {
      return {
        addClass: !addClass,
      };
    });
  };

  render() {
    const { addHaburger, addClass } = this.state;
    return (
      <div className='App'>
        <div className='hed'>
          <span className='hed2' onClick={this.addMenu}>
            {addHaburger}
          </span>
        </div>
        <div
          className={`item ${addClass ? 'itemAdd' : ''}`}
          onClick={this.addMenu}
        >
          <p>Home</p>
          <p>About</p>
          <p>Contact</p>
          <span className='spanClose' onClick={this.addMenu}>
            <i class='fas fa-times'></i>
          </span>
        </div>
      </div>
    );
  }
}
