import React from 'react';
import './App.css';

export default class App extends React.Component {
  state = {
    add: 'add',
    addText: <i class='far fa-arrow-alt-circle-left'></i>,
    delText: <i class='far fa-arrow-alt-circle-right'></i>,
    addClass: false,
  };

  changeInput = (event) => {
    this.setState({
      pageTitle: event.target.value,
    });
  };

  changeList = () => {
    this.setState({
      addClass: true,
    });
  };

  render() {
    const styles = {
      border: '3px solid black',
      width: '300px',
      height: '25px',
      marginTop: '20px',
    };

    const addStyle = {
      height: '31px',
      border: '3px solid black',
      width: '50px',
    };

    const pstyle = {
      height: '25px',
      width: '300px',
      border: '3px solid black',
      display: 'flex',
      justifyContent: 'space-between',
      marginTop: '20px',
      alignItems: 'center',
      fontSize: '23px',
      transition: '.3s',
    };

    const newClass = 'item';

    const { addClass } = this.state;

    if (addClass) {
      newClass += 'item2';
    }

    const { add, addText, delText } = this.state;

    return (
      <div className='App'>
        <div className='btn'>
          <input type='text' style={styles}></input>
          <button style={addStyle} onclick={this.changeInput}>
            {add}
          </button>
        </div>
        <div className='list'>
          <div style={pstyle} className={newClass}>
            <p>{this.state.pageTitle}</p>
            <p>
              <span className='span1'>{addText}</span>
              <span className='span2' onClick={this.changeList}>
                {delText}
              </span>
            </p>
          </div>
        </div>
      </div>
    );
  }
}
