import React from "react";
import { Field, reduxForm } from "redux-form";
import {
  maxLengthCreatre,
  recuired,
} from "../conponents/common/validators/validator";
import { Input } from "../conponents/common/FormsControls/formscontrols";
import { PostLogin } from "../redux/LoginSt";

let MXlght10 = maxLengthCreatre(10);

const LoginForm = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
      <div>
        <Field
          placeholder={"Login"}
          name={"login"}
          component={Input}
          validate={[recuired, MXlght10]}
        />
      </div>
      <div>
        {" "}
        <Field
          placeholder={"password"}
          name={"password"}
          component={Input}
          validate={[recuired, MXlght10]}
        />
      </div>
      <div>
        {" "}
        <Field
          type={"checkbox"}
          component={Input}
          validate={[recuired, MXlght10]}
          name={"rememberMe"}
        />
        remember me
      </div>
      <div>
        {" "}
        <button> login </button>
      </div>
    </form>
  );
};

const LoginReduxForm = reduxForm({
  form: "login",
})(LoginForm);

const Login = (props) => {
  const onSubmit = (Login) => {
    PostLogin(Login);
  };
  return (
    <div>
      <h1>LOGIN</h1>
      <LoginReduxForm onSubmit={onSubmit} />
    </div>
  );
};

export default Login;
