import React from "react";
import Profile from "./Profile";
import { connect } from "react-redux";
import { getStatus, updateStatus, VUprofile } from "../../redux/dr";
import { withRouter } from "react-router-dom";
import { compose } from "redux";

class ProfileC extends React.Component {
  componentDidMount() {
    let userid = this.props.match.params.userid;
    if (!userid) {
      userid = this.props.userId;
    }
    this.props.VUprofile(userid);
    this.props.getStatus(userid);
  }

  render() {
    return (
      <Profile
        {...this.props}
        profile={this.props.profile}
        status={this.props.status}
        updateStatus={this.props.updateStatus}
      />
    );
  }
}

let mapStateToProps = (state) => ({
  profile: state.myPage.profile,
  status: state.myPage.status,
  userId: state.auth.userId,
  isAuth: state.auth.isAuth,
});

export default compose(
  connect(mapStateToProps, { VUprofile, getStatus, updateStatus }),
  withRouter
)(ProfileC);
