import React from "react";
import Post from "../Post/Post";
import m from "./MyPosts.module.css";
import { Field, reduxForm } from "redux-form";

const TextareaForm = (props) => {
  let Ofmsg = props.mymsg.map((pst) => {
    return <Post img={pst.img} ike={pst.ike} msg={pst.msg} id={pst.id} />;
  });

  let clickText = React.createRef();

  let clicButtn = (e) => {
    e.preventDefault();
    props.add();
  };

  let addPost = () => {
    let text = clickText.current.value;
    props.change(text);
  };

  return (
    <div className={m.mypost}>
      <div>My posts</div>
      <form onSubmit={props.handleSubmit}>
        <Field name={"textarea"} component={"textarea"} />
        <button onClick={clicButtn}>add text</button>
      </form>
      <div>New Post</div>
      <div className="posts">{Ofmsg}</div>
    </div>
  );
};

const MyPosts = reduxForm({
  form: "textarea",
})(TextareaForm);

export default MyPosts;
