import * as axios from "axios";

let instacne = axios.create({
  withCredentials: true,
  baseURL: "https://social-network.samuraijs.com/api/1.0/",
  headers: {
    "API-KEY": "759ff387-cc25-472b-95e3-bb182814d4b3",
  },
});

export const UsersApi = {
  getUsers(mpageq = 1, mklient = 10) {
    return instacne
      .get(`users?page=${mpageq}&count=${mklient}`)
      .then((response) => response.data);
  },
  getUsers2(id) {
    return instacne.get(`follow/${id}`).then((response) => response.data);
  },
};

export const getUsers3 = () => {
  return axios.get(`https://social-network.samuraijs.com/api/1.0/auth/me`, {
    withCredentials: true,
  });
};
