import React from 'react';

export default class ToDo extends React.Component {
  state = {
    name: 'privet',
  };

  changeTitle = (event) => {
    this.setState({ name: event.target.value });
  };

  render() {
    return (
      <div className='list'>
        <div className='submit'>
          <input type='text' onChange={this.state.changeTitle} />
          <button>
            <b> Add</b>
          </button>
        </div>
        <div className=''>
          <h6>{this.props.name}</h6>
        </div>
      </div>
    );
  }
}
