import { connect } from "react-redux";
import {
  folow,
  isFetchings,
  isFLUN,
  setch,
  setTotalUserCount,
  setusers,
  unfolow,
} from "../../redux/users";
import React from "react";
import Users from "./New";
import Preloader from "../../conponents/common/Prealoader/Preloader";
import { UsersApi } from "../../API/api";

class UsersCn extends React.Component {
  componentDidMount() {
    this.props.isFetchings(true);
    UsersApi.getUsers(this.props.mpageq, this.props.mklient).then((data) => {
      this.props.isFetchings(false);
      this.props.setusers(data.items);
      this.props.setTotalUserCount(data.totalCount);
    });
  }
  onPageChanged = (usermk) => {
    this.props.isFetchings(true);
    this.props.setch(usermk);
    UsersApi.getUsers(usermk, this.props.mklient).then((data) => {
      this.props.isFetchings(false);
      this.props.setusers(data.items);
    });
  };

  render() {
    return (
      <>
        {this.props.isFetching ? <Preloader /> : null}
        <Users {...this.props} onPageChanged={this.onPageChanged} />
      </>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersReducer.users,
    mpage: state.usersReducer.mpage,
    totalUsersCount: state.usersReducer.totalUsersCount,
    mpageq: state.usersReducer.mpageq,
    isFetching: state.usersReducer.isFetching,
    nkisFLUN: state.usersReducer.toggleFLUN,
  };
};

export default connect(mapStateToProps, {
  folow,
  unfolow,
  setusers,
  setch,
  setTotalUserCount,
  isFetchings,
  isFLUN,
})(UsersCn);
