const D_R = 'BRAT_JAN';
const D_R_R = 'AB_JAN';

const DDRS = (state, action) => {
  switch (action.type) {
    case D_R:
      let addMsg = {
        msg: state.newmsg,
      };
      state.messages.push(addMsg);
      state.newmsg = '';
      return state;
    case D_R_R:
      state.newmsg = action.newMsg;
      return state;
    default:
      return state;
  }
};

export const ar = () => ({
  type: D_R,
});

export const er = (text) => ({
  type: D_R_R,
  newMsg: text,
});

export default DDRS;
