import React from 'react';
import './App.css';

export default class App extends React.Component {
  state = {
    addHaburger: <i class='fas fa-bars'></i>,
    addClass: false,
  };

  addMenu = () => {
    this.setState(({ add }) => {
      return {
        addClass: !add,
      };
    });
  };

  render() {
    const { addHaburger, addClass } = this.state;

    return (
      <div className='App'>
        <span className='hed' onClick={this.addMenu}>
          {addHaburger}
        </span>
        <div className={`${addClass ? 'itemAdd' : 'item'}`}></div>
      </div>
    );
  }
}
