}


    

    enterPerson = () => {
        console.log("enter")
        this.setState({
        done: !this.state.done
        })
      }
    render() {

       const style = {
           border: "2px solid",
           width: "250px",
           margin: "0 auto",
        
       } 
let newClass = "list";
       if(this.state.done) {
           newClass += " new"
       }
    const {item} = this.props
// const {style2} = this.state;
    return (
        <div style={style}  className={newClass}>
            <h2>{this.props.name}</h2>
           <p>
      {this.props.proff}
