import React from 'react';
import './App.css';

export default class Menu2 extends React.Component {
  state = {
    friends: [
      {
        id: 1,
        icon: <i class='fas fa-user-friends'></i>,
        name: 'For you and your family',
        text: 'We protect vehicles, property, pets and events.',
        more: 'read more',
      },
    ],
  };
  render() {
    return (
      <div className='menu2'>
        <h2>Insurance and investing for your many sides</h2>
        <h3>
          WE'VE BEEN PROTECTING PEOPLE, BUSINESSES AND FUTURES FOR MORE THAN 90
          YEARS.
        </h3>
        {this.state.friends.map((friend) => {
          return (
            <div key={friend.id} className='friend'>
              <span>{friend.icon}</span>
              <h2>{friend.name}</h2>
              <p>{friend.text}</p>
              <h4>{friend.more}</h4>
            </div>
          );
        })}
      </div>
    );
  }
}
