import React from 'react';

export default class Log extends React.Component {
  state = {
    em: '',
    ps: '',
    add: false,
  };

  Ren = (eve) => {
    eve.preventDefault();
    this.props.Ren(this.state.em, this.state.ps);
  };
  changeEmail = (ev) => {
    this.setState({ em: ev.target.value });
  };
  changePassword = (ev) => {
    this.setState({ ps: ev.target.value });
  };

  render() {
    const Done = () => {
      if (this.input.minLength === '6') {
        this.setState(({}) => {
          return {
            add: true,
          };
        });
      } else {
        this.setState(({}) => {
          return {
            add: true,
          };
        });
      }
    };
    return (
      <form onSubmit={this.Ren}>
        <input type='email' placeholder='Email' onChange={this.changeEmail} />
        <input
          className={`${this.state.add ? 'itt' : ''}`}
          type='password'
          placeholder='Password'
          minLength='6'
          onChange={this.changePassword}
          onClick={Done}
        />
        <a href='#Log'>
          {' '}
          <button>
            <b> Login in</b>
          </button>
        </a>
      </form>
    );
  }
}
