import React from 'react';
import d from './Dialog.module.css';
import Dialogitem from './Dialogitem/Dialogitem';
import Message from './Message/Message.jsx';
import { NavLink } from 'react-router-dom';
import { ar, er } from '../../../src/redux/dr.js';

let Dialog = (props) => {
  let clickText = React.createRef();

  let Add = (e) => {
    e.preventDefault();
    props.dispatch(ar());
  };
  debugger;
  let onchmsg = () => {
    let text = clickText.current.value;
    props.dispatch(er(text));
  };

  return (
    <div className={d.item}>
      <div className={d.dialogs}>
        {props.stat.person.map((item) => {
          return <Dialogitem name={item.name} id={item.id} />;
        })}
      </div>
      <div className={d.message}>
        {props.stat.messages.map((ms) => {
          return <Message name={ms.msg} />;
        })}
      </div>
      <textarea
        rows='12px'
        cols='13px'
        ref={clickText}
        value={props.stat.newmsg}
        onChange={onchmsg}
      />
      <button onClick={Add}>add</button>
    </div>
  );
};

export default Dialog;
