import { Component } from 'react';
import './App.css';

export default class App extends Component {
  state = {
    text: 'barev',
  };

  onch = (text) => {
    this.setState({
      text: text.target.value,
    });
  };

  onck = () => {
    this.setState({
      text: this.onch,
    });
  };

  render() {
    return (
      <div className='App'>
        <input type='text' placeholder='text' onChange={this.onch} />
        <p>{this.state.text}</p>
        <button onClick={this.onch}>click</button>
      </div>
    );
  }
}
