import React from 'react';
import './SectionOne.css';

export default class SectionOne extends React.Component {
  state = {
    bar: false,
    barlus: [
      { inchka: <i class='fas fa-heartbeat'></i> },
      { inchka: <i class='fas fa-heartbeat'></i> },
    ],
  };

  Ber = () => {
    this.setState(({ bar }) => {
      return {
        bar: !bar,
      };
    });
  };

  render() {
    return (
      <div className='sectionone'>
        <i class='fas fa-heartbeat' onClick={this.Ber}></i>
        {this.state.barlus.map((fr) => {
          return (
            <h2 className={`ba ${this.state.bar ? 'ba2' : ''}`}>{fr.inchka}</h2>
          );
        })}
      </div>
    );
  }
}
