import React, { useState } from "react";
import MicOff from "../icons/mic-off";
import { hexToCSSFilter } from "hex-to-css-filter";

export default function App() {
    const [hexColor, setHexColor] = useState("#000");
    const [shadow, setShadow] = useState("");

    let cssFilterValue = "";
    const cssFilter = hexToCSSFilter(hexColor, {
        acceptanceLossPercentage: 1,
        maxChecks: 10
    });
    cssFilterValue = cssFilter.filter.replace(";", "");
    //semi-colon should be removed from the string

    const shadowColor =
        shadow === "dark"
            ? "drop-shadow(1px 1px 1px rgba(0,0,0,0.5))"
            : shadow === "light"
                ? "drop-shadow(1px 1px 1px rgba(255,255,255,0.5))"
                : "";

    return (
        <div className="App">
            <h1>Select a color</h1>
            <div>
                <label htmlFor="color-picker">
                    <p>Pick a color to change icon color</p>
                </label>
                <input
                    name="color-picker"
                    aria-label="icon-color-picker"
                    type="color"
                    value={hexColor}
                    onChange={(e) => setHexColor(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="shadow">
                    <p>Show dark shadow</p>
                </label>
                <select name="shadow" onChange={(e) => setShadow(e.target.value)}>
                    <option value="dark">Dark shadow</option>
                    <option value="light">Light shadow</option>
                    <option value="no">No shadow</option>
                </select>
            </div>
            <div>
                <h2>Original svg</h2>
                <MicOff />
            </div>
            <div>
                <h2>Custom svg</h2>
                <MicOff style={{ filter: `${cssFilterValue} ${shadowColor}` }} />
            </div>
        </div>
    );
}
