import React from 'react';
import ToDo from './Todo';
import List from './List';
import './App.css';

export default class ToList extends React.Component {
  state = {
    person: [{ name: 'Text', id: 1 }],
  };

  // onDelete = (index) => {
  //   const Del = { ...this.state.person };
  //   Del.splice(index, 1);

  //   this.setState(({ person }) => {
  //     return { person: Del };
  //   });
  // };
  onDelete = (index) => {
    const newTodo = [...this.state.person];
    newTodo.splice(index, 1);

    this.setState(({ todoList }) => {
      return { Del: newTodo };
    });
  };

  addItem = (text) => {
    const newList = {
      name: text,
      id: this.newId++,
    };
    this.setState(({ person }) => {
      const myLastTodo = [...person, newList];
      return { person: myLastTodo };
    });
  };

  render() {
    return (
      <div className='box'>
        <ToDo addItem={this.addItem} />
        {this.state.person.map((item) => {
          return <List {...item} onDelete={this.onDelete} />;
        })}
      </div>
    );
  }
}
