import React from 'react';
import './App.css';

export default class App extends React.Component {
  state = {
    addHaburger: <i class='fas fa-bars'></i>,
    addClass: false,
  };

  addMenu = () => {
    this.setState(({ add }) => {
      return {
        addClass: !add,
      };
    });
  };

  render() {
    const { addHaburger, addClass } = this.state;
    return (
      <div className='App'>
        <div className='hed'>
          <span className='hed2' onClick={this.addMenu}>
            {addHaburger}
          </span>
        </div>
        <div className={`${addClass ? 'itemAdd' : 'item'}`}>
          <p>Home</p>
        </div>
      </div>
    );
  }
}
