import avatarka from '../image/down2.jpg';

let store = {
  _rrerenderInfo() {},

  _state: {
    myPage: {
      person: [
        { name: 'Sasha', id: '1' },
        { name: 'Tyom', id: '2' },
        { name: 'Viktoria', id: '3' },
        { name: 'Ann', id: '4' },
        { name: 'Ani', id: '5' },
      ],
      messages: [
        { msg: 'Hi' },
        { msg: 'How is your komasutra?' },
        { msg: 'Yo' },
      ],
      newmsg: 'Hi',
    },
    messagePage: {
      mymsg: [
        { img: avatarka, ike: 'like', msg: 'barior axperner', id: 1 },
        { img: avatarka, ike: 'like', msg: 'inchka chka', id: 2 },
      ],
      adText: 'yo yo',
    },
    friendsPage: {
      friend: [{ title: 'FRIENDS' }],
    },
  },

  getStat() {
    return this._state;
  },
  subscribe(observer) {
    this._rrerenderInfo = observer;
  },

  dispatch(action) {
    if (action.type === 'ADD-POST') {
      let addPost = {
        msg: this._state.messagePage.adText,
        img: avatarka,
        ike: 'like',
      };
      this._state.messagePage.mymsg.push(addPost);
      this._state.messagePage.adText = '';
      this._rrerenderInfo(this._state);
    } else if (action.type === 'UPDATE-NEW-POST-TEXT') {
      this._state.messagePage.adText = action.newText;
      this._rrerenderInfo(this._state);
    }
  },

  back(action) {
    if (action.type === 'BRAT_JAN') {
      let addMsg = {
        msg: this._state.myPage.newMsg,
      };
      this._state.myPage.messages.push(addMsg);
      this._state.myPage.newmsg = '';
      this._rrerenderInfo(this._state);
    } else if (action.type === 'AB_JAN') {
      this._state.myPage.newmsg = action.newMsg;
      this._rrerenderInfo(this._state);
    }
  },
};

export const arajin = () => ({
  type: 'ADD-POST',
});

export const erkrord = (text) => ({
  type: 'UPDATE-NEW-POST-TEXT',
  newMsg: text,
});

export const ar = () => ({
  type: 'BRAT_JAN',
});

export const er = (text) => ({
  type: 'AB_JAN',
  newMsg: text,
});

export default store;
