import { Component, Fragment } from 'react';
import Ake from '../../images/ake.jpg';
import John from '../../images/John.jpg';
import Lia from '../../images/Lia.jpg';
import Cardsitem from './Cards-item';

export default class Card extends Component {
  state = {
    titleLife: 'LifeCicle',
    card: [
      {
        img: John,
        name: 'Ake',
        age: 20,
        proff: 'Doctor',
      },
      {
        img: Lia,
        name: 'Lia',
        age: 21,
        proff: 'Javascript',
      },
      {
        img: Ake,
        name: 'John',
        age: 25,
        proff: 'Frontend',
      },
    ],
  };

  aboutPage = () => {
    this.props.history.push({ pathname: '/' });
  };
  titleChange = (event) => {
    this.setState({ titleLife: event.target.value });
  };
  componentWillReceiveProps(nextProps, nextState) {
    console.log('componentWillReceiveProps', nextProps, nextState);
  }
  shouldComponentUpdate(nextProps, nextState) {
    console.log('shouldComponentUpdate', nextProps, nextState);
    return true;
  }

  componentWillUpdate(nextProps, nextState) {
    console.log('componentWillUpdate', nextProps, nextState);
  }
  componentDidUpdate() {
    console.log('componentDidUpdate');
  }
  render() {
    console.log('Card render');
    console.log(this.state.titleLife);
    return (
      <Fragment>
        <h1>{this.state.titleLife}</h1>
        <button onClick={this.aboutPage}>click about page</button>
        <div className='parent-card'>
          {this.state.card.map((item, index) => {
            return (
              <Cardsitem {...item} key={index} index={index} {...this.props} />
            );
          })}
        </div>
        <input type='text' onChange={this.titleChange} />
      </Fragment>
    );
  }
}
